import React, { useState } from "react";
import ExpensesFilter from "./ExpensesFilter";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";

const Expenses = (props) => {
  const [dateChanged, setDate] = useState("2020");

  const dateChangedHandler = (enteredDate) => {
    setDate(enteredDate);
    const newDisplayedExpenses = props.item.filter((expenseItem) => {
      let year = expenseItem.props.date;
      let newYear = year.getFullYear().toString();
      return newYear === enteredDate;
    });
    setFilter(newDisplayedExpenses);
  };

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={dateChanged}
          onDateChange={dateChangedHandler}
        />

        {props.items.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}

        {/* {props.items.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))} */}
      </Card>
    </div>
  );
};

export default Expenses;
